 // Initialisieren des Steuerelements im Load-Ereignis des Formulars
  private void StartForm_Load(object sender, System.EventArgs e)
{
   // Schema einlesen
   string schemaFolder = Path.Combine(Application.StartupPath, "Schemas");
   DirectoryInfo di = new DirectoryInfo(schemaFolder);
   foreach (FileInfo fi in di.GetFiles("*.xml"))
   {
      this.cboSchema.Items.Add(fi.Name);
   }
   this.dataList.Legend.Visible = true;
   this.dataList.Legend.MinimalWidth = 100;
   // Schnellfilter definieren
   this.dataList.Legend.QuickFilterResetIcon = Resources.All;
   this.dataList.Legend.QuickFilters.Add(new DataListStatusQuickFilter(Resources.New, "New", 1, 0));
   this.dataList.Legend.QuickFilters.Add(new DataListStatusQuickFilter(Resources.OK, "Finished", 1, 1));
   this.dataList.Legend.QuickFilters.Add(new DataListStatusQuickFilter(Resources.Canceled, "Canceled", 1, 2));
   this.dataList.Legend.QuickFilters.Add(new DataListStatusQuickFilter(Resources.Error, "Error", 1 , 3));
   this.dataList.Legend.QuickFilters.Add(new DataListExpressionQuickFilter(Resources.Quickfilter, null, 2, "Ort = 'Köln'"));
   this.dataList.Legend.QuickFilters.Add(new DataListExpressionQuickFilter(Resources.Quickfilter, null, 2, "Ort = 'Dublin'"));
   this.dataList.Legend.QuickFilters.Add(new DataListExpressionQuickFilter(Resources.Quickfilter, null, 2, "Ort = 'Syndney'"));

   // Spalten definieren
   this.dataList.Columns.Add(new DataListStatusColumn("Status", "Status", 50, -1, HorizontalAlignment.Left, true, true));
   this.dataList.Columns.Add(new DataListIdColumn("Id", "Id", 50, -1, false, HorizontalAlignment.Left, true, true, true, null));
   this.dataList.Columns.Add(new DataListPlainTextColumn("Vorname", "Vorname", 150, -1, false, HorizontalAlignment.Left, true, true, true, null, false));
   this.dataList.Columns.Add(new DataListPlainTextColumn("Nachname", "Nachname", 150, -1, false, HorizontalAlignment.Left, true, true, true, null, false));
   DataListColumn column = this.dataList.Columns.Add(new DataListPlainTextColumn("Ort", "Ort", 150, -1, false, HorizontalAlignment.Left, true, true, true, null, false));
   column.ColorConditions.Add(new ColorCondition(
      new EqualsCondition("Dublin"), Color.White, Color.Navy, Color.White, Color.Blue));
   this.dataList.Columns.Add(new DataListDateTimeColumn("Geburtsdatum", "Geburtsdatum", 150, -1, false, HorizontalAlignment.Left, true, true, true, null, false));
   this.dataList.RowInfoObjects.Add(new DataListRowEnabledInfo("Enabled"));

 	// Daten einlesen (hier nicht implementiert) und anbinden
 	this.dataList.DataTable = GetData();
 }

