para.code:
private void changeToEnglish()
{
   this.aoVehicleList1.Legend.RecordStates[0].Text = "Old";
   this.aoVehicleList1.Legend.RecordStates[1].Text = "New";
   this.aoVehicleList1.Columns[0].Name = "Status";
   this.aoVehicleList1.Columns[1].Name = "Id";
   this.aoVehicleList1.Columns[2].Name = "First name";
   this.aoVehicleList1.Columns[3].Name = "Last name";
   this.aoVehicleList1.Columns[4].Name = "Birthdate";

   this.aoVehicleList1.Refresh();
}

// Zwischenspeichern der Primärschlüssel
StringCollection primaryKeys = new StringCollection();
for (int i = 0; i < this.aoVehicleList1.SelectedRows.Count; i++)
{
   primaryKeys.Add(this.aoVehicleList1.SelectedRows[i]["Id"].ToString());
}

// Neu-Einlesen des DataTable-Objekts
this.aoVehicleList1.DataTable.Clear();
this.aoVehicleList1.RefreshOnDataTableChanges = false;
for (int i = 0; i < 1000; i++)
{
   DataRow dataRow = this.aoVehicleList1.DataTable.NewRow();
   dataRow["Id"] = i;
   dataRow["Status"] = (i % 2 == 0 ? 0 : 1);
   dataRow["Vorname"] = "First Name " + i;
   dataRow["Nachname"] = "Last Name " + i;
   this.aoVehicleList1.DataTable.Rows.Add(dataRow);
}
this.aoVehicleList1.RefreshOnDataTableChanges = true;

// Das Steuerelement aktualisieren
this.aoVehicleList1.Refresh(primaryKeys);

remarks.para:
Über die Eigenschaft  erhalten Sie eine Referenz auf die vom Benutzer aktuell
angeklickte, verschobene oder in der Größe veränderte Spalte. So können Sie z. B. im DoubleClick-Ereignis
herausfinden, welche Zelle der Benutzer angeklickt hat:

private void aoVehicleList1_DoubleClick(object sender, System.EventArgs e)
{
  if (this.aoVehicleList1.SelectedRows.Count > 0)
  {
     DataRowView row = this.aoVehicleList1.SelectedRows[this.aoVehicleList1.SelectedRows.Count - 1];
     if (this.aoVehicleList1.CurrentColumn != null)
     {
        if (row[this.aoVehicleList1.CurrentColumn.DataField] != DBNull.Value)
        {
           MessageBox.Show(row[this.aoVehicleList1.CurrentColumn.DataField].ToString());
        }
        else
        {
           MessageBox.Show("Spalte ist leer");
        }
     }
     else
     {
        MessageBox.Show("Keine Spalte ist aktuell");
     }
  }
  else
  {
     MessageBox.Show("Keine Zeile ist aktuell");
  }
}

example.code:
// Einlesen des Schemas in der Main-Methode
static void Main() 
{
	// Schema für die AUTOonline-Steuerelemente einlesen
	string schemaFileName = Path.Combine(Application.StartupPath, @"AOControlsSchema1.xml");
	if (AOControlSchema.ReadSchema(schemaFileName) == false)
	{
		// Fehler auslesen ...
		string[] errors = new string[AOControlSchema.Errors.Count];
		AOControlSchema.Errors.CopyTo(errors, 0);
		string errorInfo = String.Join("\r\n", errors);

		// ... und ausgeben
		MessageBox.Show("Fehler beim Einlesen des Schema:\r\n" + errorInfo);
	}

	Application.Run(new DemoForm());
}
// Initialisieren des Steuerelements im Load-Ereignis des Formulars
private void DemoForm_Load(object sender, System.EventArgs e)
{
	// Statuswerte definieren
	string appPath = Application.StartupPath;
	this.aoVehicleList1.Legend.RecordStates.Add(new Bitmap(appPath + "\\New.bmp"), "Neu");
	this.aoVehicleList1.Legend.RecordStates.Add(new Bitmap(appPath + "\\Old.bmp"), "Alt");

	// Definieren der verfügbaren Farben für individuelle Zeileneinfärbungen
	this.aoVehicleList1.RowColors.Add(Color.AliceBlue);
	this.aoVehicleList1.RowColors.Add(Color.WhiteSmoke);
	this.aoVehicleList1.RowColors.Add(Color.LightSteelBlue);

	// Spalten definieren
	VehicleListColumn column = this.aoVehicleList1.Columns.Add("Id", VehicleListColumnType.Id, "Id", 150, false, false);
	column = this.aoVehicleList1.Columns.Add("", VehicleListColumnType.Status, "Status", 0, false, true);
	column = this.aoVehicleList1.Columns.Add("Vorname", VehicleListColumnType.Text, "FirstName", 200, false, true);
	column.IncludeInUserFilter = true;
	column = this.aoVehicleList1.Columns.Add("Nachname", VehicleListColumnType.Text, "LastName", 150, false, true);
	column.IncludeInUserFilter = true;
	column = this.aoVehicleList1.Columns.Add("Geburtsdatum", VehicleListColumnType.DateTime, "Birthdate", 50, false, true, "dd.MM.yyyy hh:mm");
	column = this.aoVehicleList1.Columns.Add("", VehicleListColumnType.ColorInfo, "ColorIndex", 0, false, false);
  column = this.aoVehicleList1.Columns.Add("Image", VehicleListColumnType.Image, "Image", 150, false, true, null, HorizontalAlignment.Center);

	// Daten einlesen (hier nicht implementiert) und anbinden
	this.aoVehicleList1.DataTable = GetData();
}

