private void scanControl(Control c)
{
	// Den Namen des übergebenen Steuerelements ausgeben
	this.lblResult.Text += Environment.NewLine + c.Name;
	// Rekursiv die Child-Steuerelement durchgehen
	if (c.HasChildren && Utils.SkipChildControls(c) == false)
    {
	    foreach (Control child in c.Controls)
        {
	       this.scanControl(child);
        }
    }
}

