private int rtfPrinting_FirstCharIndexOnNewPage;
private void printDocument_BeginPrint(object sender, System.Drawing.Printing.PrintEventArgs e)
{
   this.rtfPrinting_FirstCharIndexOnNewPage = 0;
}

private void printDocument_PrintPage(object sender, System.Drawing.Printing.PrintPageEventArgs e)
{
   // Berechnen des Inhalts einer Seite ab dem aktuell zu druckenden Zeichen
   // und berechnen des Index des nächsten zu druckenden Zeichens
   this.rtfPrinting_FirstCharIndexOnNewPage = this.rtfInfo.FormatRange(false, e, this.rtfPrinting_FirstCharIndexOnNewPage, this.rtfInfo.TextLength);
   // Ermitteln, ob weitere Seiten gedruckt werden müssen
   if (this.rtfPrinting_FirstCharIndexOnNewPage < this.rtfInfo.TextLength)
   {
      e.HasMorePages = true;
   }
   else
   {
      e.HasMorePages = false;
   }
}

