// Info-Hintergrund-Bild ermitteln
Bitmap logo = Globals.ResourceHandler.GetBitmap("VALUEonline-Info.png");

// Die E-Mail-Basisdaten erzeugen
AutoOnlineMailBaseData mailBaseData = new AutoOnlineMailBaseData
{
   MemberID = AppSettings.Customer.Id,
   Sender = AppSettings.Customer.EMail,
   Recipients = new List<string>
   {
      AppSettings.Customer_SendDataPerMail.AutoonlineHelpDeskMailAddress
   },
   SmtpServer = AppSettings.Customer_SendDataPerMail.SmtpServer,
   SmtpPort = AppSettings.Customer_SendDataPerMail.MailSMTPPort,
   SmtpUserName = AppSettings.Customer_SendDataPerMail.MailAnonymousLogin == false 
      ? AppSettings.Customer_SendDataPerMail.MailUserName : null,
   SmtpPassword = AppSettings.Customer_SendDataPerMail.MailAnonymousLogin == false 
      ? AppSettings.Customer_SendDataPerMail.MailPassword : null,
   ConfigurationFileName = Program.UserConfigurationFullFilename
};

// Infos zu den Protokolldateien anlegen
List<LogFileInfo> logFileInfos = new List<LogFileInfo>
{
   new LogFileInfo(Program.FatalLogFileName, "Fatale Fehler"),
   new LogFileInfo(Program.ErrorLogFileName, "Fehler"),
   new LogFileInfo(Program.InfoLogFileName, "Infos"),
   new LogFileInfo(Program.DebugLogFileName, "Debug")
}

// Benutzerdefinierte Schalter anlegen
List<AOAboutForm.ButtonInfo> userDefinedButtons = new List<AOAboutForm.ButtonInfo>
{
   new AOAboutForm.ButtonInfo("ShowSettings", "Show Settings"),
   new AOAboutForm.ButtonInfo("ShowConfig", "Show Configuration")
}

// Das AOAboutForm erzeugen und initialisieren
AOAboutForm aboutForm = new AOAboutForm(logFileInfos, mailBaseData, logo, userDefinedButtons);
if (Startup.StartupOptions[StartupOption.Terminal])
{
   aboutForm.FadeIn = false;
   aboutForm.FadeOut = false;
}

// Die Ereignisse zuweisen
aboutForm.BeforeSendingInfoMail += new EventHandler<AOAboutForm.SendMailCancelEventArgs>(this.aboutForm_BeforeSendingInfoMail);
aboutForm.UserDefinedButtonClick += new EventHandler<AOAboutForm.UserDefinedButtonClickEventArgs>(this.aboutForm_UserDefinedButtonClick);

// Das AboutForm anzeigen
aboutForm.ShowDialog();
